const DiaryItem = ({    
    onRemove,
    id, 
    author, 
    content, 
    emotion, 
    created_date,
    }) => {

    const handleRemove = () => {
        if(window.confirm(`${id}번째 일기를 정말 삭제하시겠습니까?`)) {
            onRemove(id); 
        }
    };

    return (
    <div className="DiaryItem">
        <div className="info">
            <span className="author_info">
                <strong className="tit">작성자 :</strong> {author} | <strong className="tit">감정점수</strong> : {emotion}
            </span>
            <br />
            <span clas sName="date">
                {new Date(created_date).toLocaleString()}
            </span>
        </div>
        <div className="content">{content}</div>
        <div className="button">
            <button onClick={() => {
                console.log(id);
                if(window.confirm(`${id}번째 일기를 정말 삭제하시겠습니까?`)) {
                    onRemove(id); 
                }
            }}>삭제하기</button>
            <button onClick={() => {
                console.log(id);
                if(window.confirm(`${id}번째 일기를 정말 삭제하시겠습니까?`)) {
                    onRemove(id); 
                }
            }}>수정하기</button>
        </div>
    </div>
    );
};

export default DiaryItem;
